mapper.select:
SELECT wt.id, wt.user_no as userNo, au.user_name as userName, DATE_FORMAT(wt.build_time,'%Y-%m-%d %H:%i:%S') as buildTime, wt.title, wt.content, wt.feedback_content as feedbackContent, wt.state, wt.receiver_user_no as receiverUserNo, DATE_FORMAT(wt.receiver_time,'%Y-%m-%d %H:%i:%S') as receiverTime, DATE_FORMAT(wt.feedback_time,'%Y-%m-%d %H:%i:%S') as feedbackTime, wt.feedback_user_no as feedbackUserNo, wt.task_type as taskType, wt.position_code as positionCode, DATE_FORMAT(wt.create_time,'%Y-%m-%d %H:%i:%S') as createTime , DATE_FORMAT(wt.edit_time,'%Y-%m-%d %H:%i:%S') as editTime FROM wlsms_task wt left join admin_user au on wt.user_no = au.user_no where 1=1
and (wt.title like concat('%',#{queryBt},'%') or wt.content like concat('%',#{queryBt},'%'))
and DATE_FORMAT(wt.build_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(wt.build_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
and wt.user_no = #{userNo}
and wt.receiver_user_no = #{receiverUserNo}
and wt.feedback_user_no = #{feedbackUserNo}
and wt.state = #{state}
and wt.task_type = #{taskType}
and wt.state > #{state}
order by id desc
